import React, { useState } from 'react';
import { PayPalScriptProvider, PayPalButtons } from "@paypal/react-paypal-js";
import Button from '@mui/material/Button';
import ButtonGroup from '@mui/material/ButtonGroup';
import Box from '@mui/material/Box';

const initialOptions = {
    "client-id": process.env.REACT_APP_PAYPAL_CLIENT_ID,
    currency: "USD",
    intent: "capture"
};

const subscriptionOptions = [
    { duration: "1 měsíc = 10USD", value: "10.00" },
    { duration: "6 měsíců = 50USD", value: "50.00" }, // Výhodnější
    { duration: "1 rok = 90USD", value: "90.00" } // Nejvýhodnější
];

/**
 * Renders a PayPal button component that handles the payment approval process.
 *
 * * testovaci kreditni karty: https://developer.paypal.com/api/rest/sandbox/card-testing/#link-creditcardgeneratorfortesting
 *
 * @return {JSX.Element} The PayPal button component.
 */
const PayPalButton = () => {
    const [selectedAmount, setSelectedAmount] = useState(subscriptionOptions[0].value);

    const handleApprove = (data, actions) => {
        // logic here
        console.log("Platba byla úspěšná!", data, actions);
        return actions.order.capture();
    };

    return (
        <Box display="flex" flexDirection="column" alignItems="center" width="100%">
        <Box maxWidth="600px" width="100%" mb={2}> {/* Nastavte maximální šířku a zarovnejte na střed */}
            <ButtonGroup variant="contained" color="primary" fullWidth>
                {subscriptionOptions.map((option, index) => (
                    <Button key={index} onClick={() => setSelectedAmount(option.value)} fullWidth>
                        {option.duration}
                    </Button>
                ))}
            </ButtonGroup>
        </Box>
        <Box maxWidth="600px" width="100%"> {/* Použijte stejnou maximální šířku pro zarovnání PayPal tlačítek */}
            <PayPalScriptProvider options={initialOptions}>
                <PayPalButtons
                    style={{ layout: "vertical", tagline: false }} /* Odstranit PayPal tagline pro lepší vzhled */
                    createOrder={(data, actions) => {
                        return actions.order.create({
                            purchase_units: [{
                                amount: { value: selectedAmount },
                            }],
                            application_context: { shipping_preference: 'NO_SHIPPING' }
                        });
                    }}
                    onApprove={handleApprove}
                />
            </PayPalScriptProvider>
        </Box>
    </Box>
    );
};

export default PayPalButton;
