import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { Box, Typography } from '@mui/material';
import MembershipStatus from '../components/MembershipStatus';
import PaymentHistory from '../components/PaymentHistory';
import PayPalButton from '../components/PayPalButton';
import { AuthContext } from '../components/AuthContext'; // TODO

const MembershipPage = () => {
  const { token } = useContext(AuthContext); // Přístup k JWT tokenu přes kontext
  const [membershipInfo, setMembershipInfo] = useState(null);
  const [paymentHistory, setPaymentHistory] = useState([]);

  useEffect(() => {
    const fetchMembershipInfo = async () => {
      try {
        const membershipResponse = await axios.get('http://localhost:8080/membership/info', { //TODO url
          headers: {
            'Authorization': `Bearer ${token}`, // Předání JWT tokenu v headeru
          }
        });
        setMembershipInfo(membershipResponse.data);
      } catch (error) {
        console.error("Error fetching membership info:", error);
      }
    };

    const fetchPaymentHistory = async () => {
      try {
        const paymentHistoryResponse = await axios.get('http://localhost:8080/membership/payments', { //TODO url
          headers: {
            'Authorization': `Bearer ${token}`, // Předání JWT tokenu v headeru
          }
        });
        setPaymentHistory(paymentHistoryResponse.data);
      } catch (error) {
        console.error("Error fetching payment history:", error);
      }
    };

    if (token) {
      fetchMembershipInfo();
      fetchPaymentHistory();
    }
  }, [token]);

  if (!token) {
    return <Typography variant="h6">Prosím, přihlaste se pro zobrazení informací o členství.</Typography>;
  }

  return (
    <Box sx={{ padding: 3 }}>
      <Typography variant="h4" sx={{ marginBottom: 2 }}>Moje Členství</Typography>
      {membershipInfo && <MembershipStatus {...membershipInfo} />}
      <PaymentHistory payments={paymentHistory} />
      <PayPalButton />
    </Box>
  );
};

export default MembershipPage;
