import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';

// Vytvoření kontextu
export const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

// Poskytovatel kontextu
export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('jwtToken'));

  useEffect(() => {
    const verifyToken = async () => {
      if (!token) return;
      try {
        // TODO Předpokládáme, že máte endpoint pro ověření platnosti tokenu?
        await axios.get('http://localhost:8080/verify-token', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        // Token je platný, zde můžete nastavit uživatele nebo jiné relevantní stavy
      } catch (error) {
        console.error("Token verification failed:", error);
        // Odstranění neplatného tokenu a vyvolání funkce logout
        logout();
      }
    };

    verifyToken();
  }, [token]);

  // Funkce pro přihlášení uživatele
  const login = (newToken, newUser) => {
    localStorage.setItem('jwtToken', newToken);
    setToken(newToken);
    setUser(newUser);
  };

  // Funkce pro odhlášení uživatele
  const logout = () => {
    localStorage.removeItem('jwtToken');
    setToken(null);
    setUser(null);
  };

  const value = {
    user,
    token,
    login,
    logout,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};
